sect1.para:
	We create the central confuguration file of the server tutorial.conf:


; we install a verbose default logger to the shell
logging	{
	stderr	{
		level DEBUG
	}
}

; one connection, one thread is enough
listen	{
	maxConnections 1
	threads 1
	socket	{
		address		*
		port		7661
		maxConnections	1
	}
}
